import React from "react";

function Tasks({ title, description, deleteTask, index }) {
  return (
    <div className="task">
      <div>
        <p>{title}</p>
        <span>{description}</span>
      </div>
      <button onClick={() => deleteTask(index)}>-</button>
    </div>
  );
}

export default Tasks;
